(function(){"use strict";var e=function(){this.$projects=$(".projects");this.projectNumber=$(".project").length;this.$buttons=$(".train-line button");this.$project=$(".project");this.counter=1;this.initialize=function(){this.setProjectsWidth();this.moveTheLine();this.getProjectDetails()};this.setProjectsWidth=function(){var e=this.projectNumber*339;this.$projects.width(e)};this.moveTheLine=function(){var e=this;e.$buttons.on("click",function(t){t.preventDefault();e.animateLine($(this))})};this.animateLine=function(e){if(e.hasClass("next")){if(this.projectNumber>this.counter){this.$projects.animate({left:"-=343"},1e3);this.counter=this.counter+1}}else if(this.counter>1){this.$projects.animate({left:"+=343"},1e3);this.counter=this.counter-1}};this.getProjectDetails=function(){var e=this;e.$project.on("click","div",function(){var t=$(this).attr("class"),n=parseInt(t.match(/(\d+)$/)[0],10)-1;e.fetchProject(n)})};this.fetchProject=function(e){$.ajax({url:document.URL,type:"POST",data:{projectId:e}}).done(function(e){$('section[role="main"]').append(e)})};this.initialize()},t=new e})();